const slideBoxes=document.querySelectorAll(".slider__box"),sliderLine=document.querySelector(".slider__slides"),btnPrev=document.querySelector(".slider__prev-btn"),btnNext=document.querySelector(".slider__next-btn");let width,count=0;function init(){width=document.querySelector(".slider__wrap").offsetWidth,sliderLine.style.width=width*slideBoxes.length+"px",slideBoxes.forEach(e=>{e.style.width=width+"px",e.style.height="auto"}),console.log(width)}function prevSlide(){--count<0&&(count=slideBoxes.length-1),rollSlide()}function nextSlide(){++count>=slideBoxes.length&&(count=0),rollSlide()}function rollSlide(){sliderLine.style.transform="translate(-"+count*width+"px"}window.addEventListener("resize",init),init(),btnPrev.addEventListener("click",prevSlide),btnNext.addEventListener("click",nextSlide);